import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import renderHTML from 'react-render-html';
import './App.css';
class App extends Component {
  state = {
    indexResponse: '',
    name: '',
    price: '',
    responseToPost: '',
  };
  arrUsers = '';
  componentDidMount() {
    this.callApi()
      //.then(res => ReactDOM.render(renderHTML(res), document.getElementById('items-list')))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const index_response = await fetch('/api/index');
    const body = await index_response.json();
    var html = '';
    if (index_response.status !== 200) throw Error(body.message);



    arrUsers = [<li>Abdul Moiz</li>,<li>John Doe</li>]


    var count = Object.keys(body).length;
    html = '<table><tr><th>Name</th><th>Price</th></tr>';
    for(var i = 0; i < count; i++) {
      console.log("Body.Name: " + body[i].Name);
      console.log("Body.Price: " + body[i].Price);
      html += `<tr><td>${body[i].Name}</td><td>${body[i].Price}</td></tr>`;
    }
    html += `</table>`;

    console.log(html);
    return html;
  };

  handleSubmit = async e => {
    e.preventDefault();
    const post_response = await fetch('/api/add-item', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: this.state.name, price: this.state.price }),
    });
    const body = await post_response.text();
    this.setState({ responseToPost: body });
  };

render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>
            My Sample Store
          </p>
        </header>

<ul>
  {arrUsers}
</ul>

        <div id="items-list"></div>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Add new inventory:</strong>
          </p>
          <label>Name
          <input
            type="text"
            id="name"
            value={this.state.name}
            onChange={e => this.setState({ name: e.target.value })}
          />
          </label>
          <label>Price
          <input
            type="text"
            id="price"
            value={this.state.price}
            onChange={e => this.setState({ price: e.target.value })}
          />
          </label>
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}
export default App;
