import React, { Component } from 'react';
import './App.css';
class App extends Component {
  state = {
    response: '',
    name: '',
    price: '',
    responseToPost: '',
  };
  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const indx_response = await fetch('/api/index');
    const body = await indx_response.json();
    if (indx_response.status !== 200) throw Error(body.message);
    return body;
  };

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/add-item', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: this.state.name, price: this.state.price }),
    });
    const body = await response.text();
    this.setState({ responseToPost: body });
  };

render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>
            My Sample Store
          </p>
        </header>
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Add new inventory:</strong>
          </p>
          <label>Name
          <input
            type="text"
            id="name"
            value={this.state.name}
            onChange={e => this.setState({ name: e.target.value })}
          />
          </label>
          <label>Price
          <input
            type="text"
            id="price"
            value={this.state.price}
            onChange={e => this.setState({ price: e.target.value })}
          />
          </label>
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}
export default App;
